    public static int[][] getCharSprite(int character) {
        int i;
            //for characters a to z
            if(character > 96 && character < 122) {
                i = character - 97;
            }
            //for characters 0 to 9
            else if(character > 47 && character < 57) {
                i = character - 23;
            }
            else {
                i = 0;
            }
        return ALPHANUM_FRAMES.get(i);
    }
